css:
@page { size: 210mm 270mm; margin: 3mm; }
  body { margin: 0; padding: 0; background: #fff; font-family: Arial, sans-serif; }
  .invoice-wrapper { box-sizing: border-box; width: 100%; max-width: 200mm; margin: 0 auto; padding: 5mm; }
  .print-format{padding:0.25mm !important;margin-top:5mm !important}

  /* Changed: Invoice title and copy label */
  .invoice-title { text-align: right; font-size: 14pt; font-weight: bold; margin-bottom: 4px; }
  .copy-label { text-align: right; font-size: 8pt; font-style: italic; margin-bottom: 8px; }

  .bold { font-weight: bold; }
  .align-right { text-align: right; }
  .main-table, .items-table, .bottom-table, .tc-sign-table { width: 100% !important; table-layout: fixed; word-wrap: break-word; }
  .main-table td, .items-table th, .items-table td, .totals-table td { padding: 2px 4px !important; }
  .main-table td { vertical-align: top; padding: 2px 2px; }

  .header-left { width: 50%; box-sizing: border-box; padding-right: 4px; }
  .header-left .company-logo { max-height: 60px; margin-bottom: 6px; }
  /* Adjusted font for company name/address */
  .header-left .company-name { font-size: 12pt; font-weight: bold; margin-bottom: 4px; }
  .header-left .company-line { font-size: 10pt; line-height: 1.3; margin-bottom: 2px; }
  .header-left .gst-pan { font-size: 10pt; margin-top: 4px; }

  .header-right { width: 50%; box-sizing: border-box; padding-left: 8px; overflow: hidden; }
  /* Changed: Amount Due color to PDF blue */
  .amount-due-box { background: #2E5F8A !important; color: #fff !important; justify-content: center; align-items: center; white-space: nowrap; font-size: 11pt; padding: 6px 12px; max-width: 100% !important; overflow: hidden; text-overflow: ellipsis; }
  .amount-due-box .amount-figure { margin-left: 6px; display: inline; font-weight: bold; }

  .invoice-details { width: 100% !important; table-layout: fixed !important; border-collapse: collapse; font-size: 9pt; }
  .invoice-details td { padding: 1px 3px !important; white-space: nowrap; font-size: 8.5pt; }
  .invoice-details .label-cell { font-weight: bold; width: 35%; overflow: hidden; text-overflow: ellipsis; }
  .invoice-details .value-cell { width: 65%; text-align: right; overflow: hidden; text-overflow: ellipsis; }

  .bill-ship-table { margin-top: 12px; }
  /* Changed: Highlight bill-to with blue background */
  .bill-to { width: 50%; vertical-align: top; padding: 8px; }
  .bill-to .section-title { font-size: 12pt; font-weight: bold; border-bottom: 1px solid #fff; margin-bottom: 4px; }
  /* Added: customer name styling */
  .bill-to .bill-name { font-size: 13pt; font-weight: bold; margin-bottom: 4px; }
  .bill-to .bill-detail { font-size: 10pt; line-height: 1.3; margin-bottom: 2px; }
  .ship-to { display:flex; flex-direction:column;align-items:flex-end }

  .irn { display:flex; flex-direction:column;align-items:flex-end}

  .items-table { border-collapse: collapse; margin-top: 12px; }
  .items-table th, .items-table td { border: 1px solid #666; padding: 4px 6px; font-size: 8.5pt; }
  .items-table th { background: #e6e6e6; font-weight: bold; text-align: center; }
  .items-table td { vertical-align: top; }
  .items-table .col-sn { width: 4%;background: #2E5F8A !important; color: #fff !important; }
  .items-table .col-desc { width: 20%; text-align: left;background: #2E5F8A !important; color: #fff !important; }
  .items-table .col-hsn { width: 18%;background: #2E5F8A !important; color: #fff !important; }
  .items-table .col-qty { width: 18%;background: #2E5F8A !important; color: #fff !important; }
  .items-table .col-rate { width: 10%;background: #2E5F8A !important; color: #fff !important; }
  .items-table .col-taxable { width: 12%;background: #2E5F8A !important; color: #fff !important; }
  .items-table .col-sgst { width: 10%; background: #2E5F8A !important; color: #fff !important;}
  .items-table .col-amt { width: 11%; background: #2E5F8A !important; color: #fff !important;}
  .items-table .col-type { width: 15%; background: #2E5F8A !important; color: #fff !important;}       
  .items-table .col-storage { width: 10%; background: #2E5F8A !important; color: #fff !important;}
  .items-table .col-charge { width: 10%; background: #2E5F8A !important; color: #fff !important;}


  .items-table .right-cell { text-align: right; }
  .items-table .subtotal-row td { background: #f9f9f9; font-weight: bold; }
  .items-table .desc-cell {font-weight: bold; font-size:8pt;}

  .bottom-table { margin-top: 12px; }
  .bank-cell { width: 40%; vertical-align: top; padding-top:5pt; }
  /*.bank-cell .section-title { font-size: 10pt; font-weight: bold; border-bottom: 1px solid #333; margin-bottom: 4px; }*/
  .bank-cell .bank-label { display: inline-block; width: 100px; font-weight: bold;font-size:8pt; }
  .bank-cell .bank-value {font-size:8pt}

  .totals-cell { width: 60%; vertical-align: top; padding-left: 20px; }
  .totals-table { width: 100%; border-collapse: collapse; font-size: 10pt; }
  .totals-table td { padding: 4px 6px; }
  .totals-table .label-cell { text-align: left; font-weight: bold; font-size:10pt; }
  .totals-table .value-cell { text-align: right; }

  .tc-sign-table { border-collapse: collapse; margin-top: 20px; }
  .tc-cell { width: 60%; vertical-align: top; font-size: 8.5pt; line-height: 1.4; }
  .tc-cell .section-title { font-size: 10pt; font-weight: bold; margin-bottom: 4px; }
  .jurisdiction-text { margin-top: 8px; font-style: italic; }

  .signature-qr-cell { width: 40%; vertical-align: top; text-align: center; }
  .signature-qr-cell .sign-label {font-size:6pt}
  .signature-box { margin-top: 10px; width: 240px; height: 80px; display: inline-block; }
  .signature-box img { max-height: 80px; max-width: 240px; }
  .signature-label { margin-top: 4px; font-size: 9pt; }
  .qr-code { max-width: 90px !important; }
