css:
@page {
  size: A4 portrait;
   margin: 5mm;
}
body {
  margin: 0;
  padding: 0;
  background: #fff;
}

.invoice-wrapper {
  box-sizing: border-box;
  width: 100%;
  max-width: 200mm;           
  margin: 0 auto;
  padding: 5mm;              
}

.bold { font-weight: bold; }
.align-right { text-align: right; }


.main-table,
.items-table,
.bottom-table,
.tc-sign-table {
  width: 100% !important;
  table-layout: fixed;
  word-wrap: break-word;
}

.main-table td,
.items-table th,
.items-table td,
.totals-table td {
  padding: 2px 4px !important;
}
.main-table td {
  vertical-align: top;
  padding: 2px 2px;
}

.header-left {
  width: 50%;
  box-sizing: border-box;
  padding-right: 4px;
}
.header-left .company-logo {
  max-height: 60px;
  margin-bottom: 6px;
}
.header-left .company-name {
  font-size: 13pt;
  font-weight: bold;
  margin-bottom: 4px;
}
.header-left .company-line {
  font-size: 8pt;
  line-height: 1.3;
  margin-bottom: 2px;
}
.header-left .gst-pan {
  font-size: 7.5pt; 
  margin-top: 4px;
  font-size: 9pt;
}

.header-right {
  width: 50%;
  box-sizing: border-box;
  padding-left: 8px;
  overflow: hidden;
}
.amount-due-box {
  background: #333 !important;
  color: #fff !important;
  justify-content: center;
  align-items: center;
  white-space: nowrap;
  font-size: 11pt;
  padding: 6px 12px; 
  max-width: 100% !important;
  overflow: hidden;
  text-overflow: ellipsis;
}

.amount-due-box::before {
  max-width: 100% !important;
  font-weight: normal;
  color: #fff;
}
.amount-due-box .amount-figure {
  margin-left: 6px;              
  display: inline;
  font-weight: bold;
}
.invoice-details {
  width: 100% !important;
  table-layout: fixed !important;
  border-collapse: collapse;
  font-size: 9pt;
}
.invoice-details td {
  padding: 1px 3px !important;
  white-space: nowrap;
  font-size: 8.5pt !important
}
.invoice-details .label-cell {
  font-weight: bold;
  width: 35% !important;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.invoice-details .value-cell {
 width: 65% !important;
 text-align: right;
 white-space: nowrap;
 overflow: hidden;
 text-overflow: ellipsis;
 text-align: right;
}

.bill-ship-table {
  margin-top: 12px;
}
.bill-to,
.ship-to {
  width: 50%;
  vertical-align: top;
}
.bill-to .section-title,
.ship-to .section-title {
  font-size: 10pt;
  font-weight: bold;
  border-bottom: 1px solid #333;
  margin-bottom: 4px;
}


.items-table {
  width: 100%;
  border-collapse: collapse;
  margin-top: 12px;
}
.items-table th,
.items-table td {
  border: 1px solid #666;
  padding: 4px 6px;
  font-size: 8.5pt;
}
.items-table th {
  background: #f0f0f0;
  font-weight: bold;
  text-align: center;
}
.items-table td {
  vertical-align: top;
}
.items-table .col-sn { width: 4%; }
.items-table .col-desc { width: 25%; text-align: left; }
.items-table .col-hsn { width: 10%; }
.items-table .col-qty { width: 8%; }
.items-table .col-rate { width: 10%; }
.items-table .col-taxable { width: 12%; }
.items-table .col-cgst,
.items-table .col-sgst { width: 10%; }
.items-table .col-amt { width: 11%; }
.items-table .right-cell { text-align: right; }
.items-table .subtotal-row td {
  background: #f9f9f9;
  font-weight: bold;
}
.bottom-table {
  margin-top: 12px;
}
.bank-cell {
  width: 40%;
  vertical-align: top;
}
.bank-cell .section-title {
  font-size: 10pt;
  font-weight: bold;
  border-bottom: 1px solid #333;
  margin-bottom: 4px;
}
.bank-details {
  font-size: 7.5pt;
  line-height: 1.4;
}
.bank-details .bank-label {
  display: inline-block;
  width: 100px;
  font-weight: bold;
}

.totals-cell {
  width: 60%;
  vertical-align: top;
  padding-left: 20px;
}
.totals-table {
  width: 100%;
  border-collapse: collapse;
  font-size: 9pt;
}
.totals-table td {
  padding: 4px 6px;
}
.totals-table .label-cell {
  text-align: left;
  font-weight: bold;
}
.totals-table .value-cell {
  text-align: right;
}

.tc-sign-table {
  width: 100%;
  border-collapse: collapse;
  margin-top: 20px;
}
.tc-cell {
  width: 60%;
  vertical-align: top;
  font-size: 8.5pt;
  line-height: 1.4;
}
.tc-cell .section-title {
  font-size: 10pt;
  font-weight: bold;
  border-bottom: 1px solid #333;
  margin-bottom: 4px;
}
.jurisdiction-text {
  margin-top: 8px;
  font-style: italic;
}

.signature-qr-cell {
  width: 40%;
  vertical-align: top;
  text-align: center;
}
.signature-box {
  margin-top: 10px;
  display: inline-block;
  width: 240px;
  height: 80px;
}
.signature-box img {
  max-height: 80px;
  max-width: 240px;
}
.signature-label {
  margin-top: 4px;
  font-size: 9pt;
}

.qr-code {
  margin-top: 12px;
  max-width: 120px;
}
